import { createElement, useEffect, useState } from "react";
import { BoxComponent } from "./BoxComponent";
import { ButtonComponent } from "./ButtonComponent";
import { LinkComponent } from "./LinkComponent";
import { ListComponent } from "./ListComponent";
import { ParagraphComponent } from "./ParagraphComponent";
import { H1Component } from "./H1Component";
import { H2Component } from "./H2Component";
import { ModalComponent } from "./ModalComponent";

const keysToComponentMap = {
    box: BoxComponent,
    p: ParagraphComponent,
    li: ListComponent,
    a: LinkComponent,
    button: ButtonComponent,
    h1: H1Component,
    h2: H2Component,
    modal: ModalComponent
}

const stylesMap = styles => {
    let mappedStyles = {}
    styles.forEach(style => {
        mappedStyles[style.name] = style.value
    });
    return mappedStyles;
}

const iterateOverChildren = object => {
    let children = []
    for (const key in object) {
        children.push(object[key])
    }
    return children
}

export const RennderComponents = config => {
    const [children, setChildren] = useState([])
    useEffect(() => {
        if(config.Children){
            let childrenToPush = []
            for (const key in config.Children) {
                childrenToPush.push(config.Children[key])
            }
            setChildren(childrenToPush)
        }
    }, [])
    if (typeof keysToComponentMap[config.Content.type] !== 'undefined') {
        return createElement(
            keysToComponentMap[config.Content.type],
            {
                props: config.Content.props ? config.Content.props : {},
                className: config.Content.className ? config.Content.className : null,
                id: config.Content.id ? config.Content.id : null,
            },
            children.length > 0 && children.map( c => RennderComponents(c))
        )
    }
}
