import { useEffect, useState, createContext } from 'react';
import logo from './logo.svg';
import './App.css';
import $ from 'jquery';
import { RennderComponents } from './components/RenderComponent';

const ModalContext = createContext(false);

function App() {
  const [componentData, setComponentData] = useState({});
  const [contextModalValue, setContextModalValue] = useState(false);
  useEffect(() => {
    $.ajax({
      url: '/data2.json',
      dataType:'json',
      cache: false,
      success: (data) => {
        setComponentData(data)
      },
      error: (xhr, status, err) => {
        console.error(err)
      }
    })
  },[])
  useEffect(() => {
    // console.log("componentData", componentData)
    // console.log("componentData.hasOwnProperty('Modal')", componentData.Children.hasOwnProperty('Modal'))
    if (componentData.Content.type === 'ModalComponent') {
      setContextModalValue(componentData.Content.props.isOpen)
    } else if (componentData.Children.hasOwnProperty('Modal')) {
      setContextModalValue(componentData.Children.Modal.Content.props.isOpen)
    }
  }, [componentData])
  return (
    <div className="App">
      {/* <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
