import { useEffect, useState } from "react"
export const BoxComponent = (props) => {
    console.log("got inside BoxComponent", props)
    const [styles, setStyles] = useState({})
    useEffect(() => {
        let style = {}
        if (props.props) {
            for (const key in props.props) {
                if (key === 'borderSize') {
                    style['borderWidth'] = props.props[key]
                } else {
                    style[key] = props.props[key]
                }
            }
        } else {
            style['borderWidth'] = "2px"
        }
        setStyles(style)
    })
    return (
        <div style={styles} className={props.className} id={props.id}>
            {props.children}
        </div>
    )
}
