import {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import $ from 'jquery';
import { RennderComponents } from './components/RenderComponent';

function App() {
  const [componentData, setComponentData] = useState({})
  const [openModal, setOpenModal] = useState(null)
  useEffect(() => {
    $.ajax({
      url: '/data2.json',
      dataType:'json',
      cache: false,
      success: (data) => {
        setComponentData(data)
      },
      error: (xhr, status, err) => {
        console.error(err)
      }
    })
  },[])
  useEffect(() => {
    // console.log("window.openModal", window.openModal)
    if (componentData && componentData.Content && componentData.Content.type === 'ModalComponent') {
      setOpenModal(componentData.Content.props && componentData.Content.props.isOpen ? componentData.Content.props.isOpen : false)
    } else if (componentData && componentData.Children && componentData.Children.hasOwnProperty('Modal')) {
      setOpenModal(componentData.Children.Modal && componentData.Children.Modal.Content && componentData.Children.Modal.Content.props.isOpen ? componentData.Children.Modal.Content.props.isOpen : false)
    }
  }, [componentData])
  function buttonAction({componentName, cbdata}) {
    console.log("entered in buttonAction")
    switch (componentName) {
      case 'modal':
        setOpenModal(cbdata)
        break;

      default:
        break;
    }
  }
  if(componentData && Object.keys(componentData).length > 0 && Object.getPrototypeOf(componentData) === Object.prototype ) return RennderComponents(componentData, openModal, buttonAction)}

export default App;
